@namespace
class SpriteKind:
    Monster = SpriteKind.create()
    FakeMonsters = SpriteKind.create()
    NormalThing = SpriteKind.create()
"""

Battery goes to zero

flashlight off (A button released)

"""
# On game start
# 
# turnOnTheDark: add your code below
def turnOnTheDark():
    global darkIsOff
    setTilemap()
    for value in sprites.all_of_kind(SpriteKind.NormalThing):
        value.set_image(sprites.read_data_sprite(value, "MUSTACHE!!!!!!!!!!!!!!!!!").image)
        value.follow(sierra, 15)
        value.set_kind(SpriteKind.Monster)
    darkIsOff = False
# makeDualityMonster: add your code below
# 
# createAllMonsters: add your code below
def createAllMonsters():
    for index in range(5):
        makeDualityMonster(monster1, normalThing1)
        makeDualityMonster(monster2, normalThing2)
        makeDualityMonster(monster3, normalThing3)
        makeDualityMonster(monster4, normalThing4)

def on_b_pressed():
    sierra.set_velocity(1000000, -10)
controller.B.on_event(ControllerButtonEvent.PRESSED, on_b_pressed)

