# redoTile -- do not delete.
def redoTile(tileImage: Image):
    global copiedTile
    copiedTile = tileImage.clone()
    copiedTile.replace(12, 13)
    copiedTile.replace(11, 1)
    copiedTile.replace(14, 4)
    tiles.cover_all_tiles(tileImage, copiedTile)

def on_on_zero(status):
    turnOnTheDark()
statusbars.on_zero(StatusBarKind.health, on_on_zero)

