def makeDualityMonster(night: Image, day: Image):
    global monsutaJanSipiwaituIke, daytimeMonster, nighttimeMonster
    monsutaJanSipiwaituIke = sprites.create(night, SpriteKind.Monster)
    daytimeMonster = sprites.create(day, SpriteKind.FakeMonsters)
    nighttimeMonster = sprites.create(night, SpriteKind.FakeMonsters)
    daytimeMonster.set_flag(SpriteFlag.INVISIBLE, True)
    daytimeMonster.set_flag(SpriteFlag.GHOST, True)
    nighttimeMonster.set_flag(SpriteFlag.INVISIBLE, True)
    nighttimeMonster.set_flag(SpriteFlag.GHOST, True)
    sprites.set_data_sprite(monsutaJanSipiwaituIke,
        "BRAAAAAAAAAAAAAINZZZZ...",
        daytimeMonster)
    sprites.set_data_sprite(monsutaJanSipiwaituIke,
        "MUSTACHE!!!!!!!!!!!!!!!!!",
        nighttimeMonster)
    monsutaJanSipiwaituIke.set_position(randint(50, 500), randint(20, 120))
    monsutaJanSipiwaituIke.follow(sierra, 15)
# Vivian wins, do not delete

