// redoTile -- do not delete.
function redoTile (tileImage: Image) {
    copiedTile = tileImage.clone()
    copiedTile.replace(12, 13)
    copiedTile.replace(11, 1)
    copiedTile.replace(14, 4)
    tiles.coverAllTiles(tileImage, copiedTile)
}
