// turnOffTheDark: ninja must complete
function turnOffTheDark () {
    // add code here
    redoTile(assets.tile`tile1`)
    redoTile(assets.tile`tile2`)
    redoTile(assets.tile`tile3`)
    redoTile(assets.tile`tile4`)
    redoTile(assets.tile`tile5`)
    redoTile(assets.tile`tile6`)
    redoTile(assets.tile`tile7`)
    darkIsOff = true
    for (let monster of sprites.allOfKind(SpriteKind.Monster)) {
        try {
            music.buzzer.playUntilDone()
            monster.setImage(sprites.readDataSprite(monster, "BRAAAAAAAAAAAAAINZZZZ...").image)
        }
        catch {
            console.log("WOOO!")
        }
        finally {
            console.log("WOOOOOOOOOOOOOOO!\nI CRASHED MAKECODE!!!!!!")
        } 
    }
}
function makeDualityMonster (night: Image, day: Image) {
    monsutaJanSipiwaituIke = sprites.create(night, SpriteKind.Monster)
    daytimeMonster = sprites.create(day, SpriteKind.FakeMonsters)
    nighttimeMonster = sprites.create(night, SpriteKind.FakeMonsters)
    daytimeMonster.setFlag(SpriteFlag.Invisible, true)
    daytimeMonster.setFlag(SpriteFlag.Ghost, true)
    nighttimeMonster.setFlag(SpriteFlag.Invisible, true)
    nighttimeMonster.setFlag(SpriteFlag.Ghost, true)
    sprites.setDataSprite(monsutaJanSipiwaituIke, "BRAAAAAAAAAAAAAINZZZZ...", daytimeMonster)
    sprites.setDataSprite(monsutaJanSipiwaituIke, "MUSTACHE!!!!!!!!!!!!!!!!!", nighttimeMonster)
    monsutaJanSipiwaituIke.setPosition(randint(50, 500), randint(20, 120))
    monsutaJanSipiwaituIke.follow(sierra, 15)
}
